"use client";

import * as LabelPrimitive from "@radix-ui/react-label";
import { Control, FieldPath, FieldValues } from "react-hook-form";

import {
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { ComponentProps } from "react";
import { MyInput } from "../base-component/my-input";

type TFormInput<
  TFieldValues extends FieldValues = FieldValues,
  TFieldName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
> = {
  name: TFieldName;
  control: Control<TFieldValues>;
  label?: string | React.ReactNode;
  formLabelProps?: React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root>;
  onChangeCallBack?: (e: React.ChangeEvent<HTMLInputElement>) => void | string;
} & Omit<ComponentProps<typeof MyInput>, "onChange" | "value">;

export const FormInput = <
  TFieldValues extends FieldValues = FieldValues,
  TFieldName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
>({
  formLabelProps,
  name,
  control,
  label,
  onChangeCallBack,
  ...props
}: TFormInput<TFieldValues, TFieldName>) => {
  return (
    <FormField
      control={control}
      name={name}
      render={({ field: { onChange, ...fieldProps } }) => (
        <FormItem>
          {label && <FormLabel {...formLabelProps}>{label}</FormLabel>}
          <FormControl>
            <MyInput
              {...fieldProps}
              {...props}
              onChange={(e) => {
                if (typeof onChangeCallBack !== "function") {
                  onChange(e);
                  return;
                }

                const formatValue = onChangeCallBack(e);
                onChange(typeof formatValue === "string" ? formatValue : e);
              }}
            />
          </FormControl>
          <FormMessage />
        </FormItem>
      )}
    />
  );
};

import * as React from "react";

import { cn } from "@/lib/utils";
import { Input } from "@/components/ui/input";

type TProps = {
  prefix?: React.ReactNode;
  suffix?: React.ReactNode;
} & Omit<React.ComponentProps<typeof Input>, "prefix" | "ref">;

function MyInput({ className, suffix, prefix, ...props }: TProps) {
  return (
    <div className="relative">
      <Input
        className={cn(
          "aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive aria-invalid:text-destructive aria-invalid:bg-[#FEF2F2]",
          "peer",
          prefix && "ps-9",
          suffix && "pe-9",
          className
        )}
        {...props}
      />
      {prefix && (
        <span className="[&>svg]:size-4 text-muted-foreground pointer-events-none absolute inset-y-0 start-0 flex items-center justify-center ps-3 text-sm peer-disabled:opacity-50">
          {prefix}
        </span>
      )}
      {suffix && (
        <span className="[&>svg]:size-4 text-muted-foreground pointer-events-none absolute inset-y-0 end-0 flex items-center justify-center pe-3 text-sm peer-disabled:opacity-50">
          {suffix}
        </span>
      )}
    </div>
  );
}

export { MyInput };
